    fun setSharedPrefForSigning(
        token: String,
        image: String?,
        name: String,
        phone_number: String,
        user_password:String,
        userType: String
    ) {
        val editor = myShared.edit()
        editor.putString(userToken, token)
        editor.putBoolean(isSignedIn, true)
        editor.putString(userImage, image)
        editor.putString(userName, name)
        editor.putString(userPhoneNumber, phone_number)
        editor.putString(userPassword, user_password)
        editor.putString(CommonConstants.userType, userType)
        editor.putString(userEmail, emailPrefix + phone_number + emailSuffix)
        editor.apply()
    }

    fun setSharedPrefForUpdateProfile(
        image: String?,
        name: String,
        phone_number: String,
    ) {
        val editor = myShared.edit()
        if (image != null){
            editor.putString(userImage, image)
        }
        editor.putString(userName, name)
        editor.putString(userPhoneNumber, phone_number)
        editor.putString(userEmail, emailPrefix + phone_number + emailSuffix)
        editor.apply()
    }

    fun logOut() {
        val editor = myShared.edit()
