    fun getStringTimeFormat(value: Float): String {
        val hrs = ((value / 3600) % 60).toLong()
        val mns = (value / 60).toLong()

        return when (hrs) {
            0L -> String.format(Locale.US, "%02d ", mns)
            else -> String.format(Locale.US, "%02d   %02d ", hrs, mns)
        }
    }

    fun convertStrUTCTimeToStrLocal(dateStr:String):String{
       val df = SimpleDateFormat("yyyy-MM-dd HH:mm:ss", Locale.US)
        df.timeZone = TimeZone.getTimeZone("UTC")
        val date: Date = df.parse(dateStr)!!
        df.timeZone = TimeZone.getDefault()
        return df.format(date)
    }

    fun getStringDifferenceBetweenTwoDates(value: Long): String {
        val diff = System.currentTimeMillis() - value
        val diffInDays: Long = TimeUnit.MILLISECONDS.toDays(diff) //  
        val diffInHours: Long = TimeUnit.MILLISECONDS.toHours(diff)
        val diffInMin: Long = TimeUnit.MILLISECONDS.toMinutes(diff)
        //  val diffInSec: Long = TimeUnit.MILLISECONDS.toSeconds(diff)
        return when {
            diffInDays > 0 -> {
                "$diffInDays "
            }
            diffInHours > 0 -> {
                "$diffInHours "
            }
            diffInMin > 0 -> {
                "$diffInMin "
            }
            else -> {
                ""
            }
        }
    }


    fun bitmapToString(context: Context, image: Int): String {
        val bmp = BitmapFactory.decodeResource(context.resources, image)
        val stream = ByteArrayOutputStream()
        bmp.compress(Bitmap.CompressFormat.PNG, 100, stream)
        val byteArray: ByteArray = stream.toByteArray()
        return Base64.encodeToString(byteArray, Base64.DEFAULT) // (android.util)
    }

    fun bitmapToString(image: Bitmap): String {
        val stream = ByteArrayOutputStream()
        image.compress(Bitmap.CompressFormat.PNG, 100, stream)
        val byteArray = stream.toByteArray()
        return Base64.encodeToString(byteArray, Base64.DEFAULT) // (android.util)
    }

    fun stringToBitmap(stringImage: String, image: ImageView) {
        val byteArray: ByteArray = Base64.decode(stringImage, Base64.DEFAULT)
        val bmp = BitmapFactory.decodeByteArray(byteArray, 0, byteArray.size)
        image.setImageBitmap(bmp)
    }

    fun getDurationFromDateUntilNow(date1:Date,date2:Date): String{// calculate age
        val diff: Long = date1.time - date2.time
        val seconds = diff / 1000
        val minutes = seconds / 60
        val hours = minutes / 60
        val days = hours / 24
        val month = days/ 30
        val years = days/365

        return when{
            years > 0 -> "$years "
            month > 0 -> "$month "
            days > 0 -> "$days "
            hours > 0 -> "$hours "
            minutes > 0 -> "$minutes "
            seconds > 0 -> "$seconds "
            else->{
                " "
            }
        }
    }


    fun checkTimeBetweenTwoTimesOrNot(
        comparativeTime: String,
        startingTime: String,
        endingTime: String,
        formatter: SimpleDateFormat
    ): Boolean {
        try {
            val calendar1 = Calendar.getInstance()
            calendar1.time = formatter.parse(startingTime)!!
            calendar1.add(Calendar.DATE, 1)

            val calendar2 = Calendar.getInstance()
            calendar2.time = formatter.parse(endingTime)!!
            calendar2.add(Calendar.DATE, 1)

            val calendar3 = Calendar.getInstance()
            calendar3.time = formatter.parse(comparativeTime)!!
            calendar3.add(Calendar.DATE, 1)

            val x = calendar3.time
            if (x.after(calendar1.time) && x.before(calendar2.time)) {
                return true
            }
            return false
        } catch (e: ParseException) {
            return false
        }
    }


    /// 1- permissions
    /// 2- isEnabledGPS
    /// 3- getCurrentLocation (point : com.google.android.gms.maps.model.LatLong?)

    fun checkLocationPermissions(activity: Activity): Boolean {
        return if (ActivityCompat.checkSelfPermission(
                activity,
                Manifest.permission.ACCESS_COARSE_LOCATION
            ) == PackageManager.PERMISSION_GRANTED && ActivityCompat.checkSelfPermission(
                activity,
                Manifest.permission.ACCESS_FINE_LOCATION
            ) == PackageManager.PERMISSION_GRANTED
        ) {
            true
        } else {
            hideProgressDialog() //
            ActivityCompat.requestPermissions(
                activity, arrayOf(
                    Manifest.permission.ACCESS_COARSE_LOCATION,
                    Manifest.permission.ACCESS_FINE_LOCATION
                ), 1000
            )
            false
        }
    }


    fun isLocationEnabled(activity: Activity): Boolean {
        val locationManager = activity.getSystemService(Context.LOCATION_SERVICE) as LocationManager
        return locationManager.isProviderEnabled(LocationManager.GPS_PROVIDER) || locationManager.isProviderEnabled(
            LocationManager.NETWORK_PROVIDER
        )
    }

    fun changeAppFontsSize(context: Context,configuration: Configuration, scale: Float) {
        configuration.fontScale = scale
        val metrics: DisplayMetrics = context.resources.displayMetrics
        val wm = context.getSystemService(WINDOW_SERVICE) as WindowManager?
        wm!!.defaultDisplay.getMetrics(metrics)
        metrics.scaledDensity = configuration.fontScale * metrics.density
        context.resources.updateConfiguration(configuration, metrics)
    }

    fun isAppRunning(context: Context, packageName: String): Boolean {
        val activityManager = context.getSystemService(Context.ACTIVITY_SERVICE) as ActivityManager
        val procInfos = activityManager.runningAppProcesses
        if (procInfos != null) {
            for (processInfo in procInfos) {
                if (processInfo.processName == packageName) {
                    return true
                }
            }
        }
        return false
    }

    private fun dpToPx(dp: Float, context: Context): Int {
        return TypedValue.applyDimension(TypedValue.COMPLEX_UNIT_DIP, dp, context.resources.displayMetrics).toInt()
    }

    fun spToPx(sp: Float, context: Context): Int {
        return TypedValue.applyDimension(TypedValue.COMPLEX_UNIT_SP, sp, context.resources.displayMetrics).toInt()
    }

    fun dpToSp(dp: Float, context: Context): Int {
        return (dpToPx(dp, context) / context.resources.displayMetrics.scaledDensity).toInt()
    }


    fun isNetworkAvailable(context: Context?): Boolean {
        if (context == null) return false
        val connectivityManager = context.getSystemService(Context.CONNECTIVITY_SERVICE) as ConnectivityManager
        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.Q) {//  API 29          else     api   29
            val capabilities = connectivityManager.getNetworkCapabilities(connectivityManager.activeNetwork)
            if (capabilities != null) {
                when {
                    capabilities.hasTransport(NetworkCapabilities.TRANSPORT_CELLULAR) -> {
                        return true
                    }
                    capabilities.hasTransport(NetworkCapabilities.TRANSPORT_WIFI) -> {
                        return true
                    }
                    capabilities.hasTransport(NetworkCapabilities.TRANSPORT_ETHERNET) -> {
                        return true
                    }
                }
            }
        } else {
            val activeNetworkInfo = connectivityManager.activeNetworkInfo
            if (activeNetworkInfo != null && activeNetworkInfo.isConnected) {
                return true
            }
        }
        return false
    }


    fun showCustomToast(context:Activity,text: String?,type: String){
        //https://github.com/GrenderG/Toasty // for custom
        when(type){
            "error"-> Toasty.error(context,text.toString(),Toasty.LENGTH_SHORT).show()
            "success"-> Toasty.success(context,text.toString(),Toasty.LENGTH_SHORT).show()
            "info"-> Toasty.info(context,text.toString(),Toasty.LENGTH_SHORT).show()
            "warning"-> Toasty.warning(context,text.toString(),Toasty.LENGTH_SHORT).show()
            "normal"-> Toasty.normal(context,text.toString(),Toasty.LENGTH_SHORT).show()
        }
    }


